import React from "react";
import MovieContext from "../services/MovieContext";
import { useContext,useState } from "react";
import {
  BiChevronLeft,
  BiChevronRight,

} from "react-icons/bi";


export default function MovieNews() {
  const { movies } = useContext(MovieContext);



  const data = [
    {
      id: 1,
      tag: "Action",
      year: 2023,
      title: "Knives Out |||",
    },
    {
      id: 2,
      tag: "Comedy",
      year: 2024,
      title: "The Lord of the Rings: The Two Towers",
    },
    {
      id: 3,
      tag: "Action",
      year: 2023,
      title: "The Marvelous Mrs. Maisel"
    },
    {
      id: 4,
      tag: "Action",
      year: 2023,
      title: "Fast X",
    },
    {
      id: 5,
      tag: "Action",
      year: 2023,
      title: "The Marvelous Mrs. Maisel"
    },
    {
      id: 6,
      tag: "Action",
      year: 2023,
      title: "Fast X",
    },
    {
      id: 7,
      tag: "Action",
      year: 2023,
      title: "The Marvelous Mrs. Maisel"
    },
    {
      id: 8,
      tag: "Action",
      year: 2023,
      title: "Fast X",
    },
  ];

  const [pageNumber, setPageNumber] = useState(1);
  const postLimit = 4;
  const lastIndex = pageNumber * postLimit;
  const firstIndex = lastIndex - postLimit;

  const numberOfPages = Math.ceil(data.length / postLimit);

  const nextPage = () => {
    if (pageNumber < numberOfPages) {
      setPageNumber(pageNumber + 1);
    }
  };
  const prevPage = () => {
    if (pageNumber > 1) {
      setPageNumber(pageNumber - 1);
    }
  };



  const whatToWatch=[...movies.slice(0,4)]

  const listOfPicks=whatToWatch.map((item)=>{
    return(
      <div className="movie_news_card" key={item.id}>
        <img
          src={require("../images/movie1.jpg")}
          className="movie_news_card_image"
          alt=""
        />
        <div className="movie_news_card_tags">
          <h2 className="movie_news_card_tag">{item.tag}</h2>
          <h2 className="movie_news_card_year">{item.year}</h2>
        </div>
        <h2 className="movie_news_card_title">
          {item.movie}
        </h2>
      </div>
    )
  })



  const listOfNewMovies = data.map((item) => {
    return (

      <div className="movie_news_card" key={item.id}>
        <img
          src={require("../images/movie1.jpg")}
          className="movie_news_card_image"
          alt=""
        />
        <div className="movie_news_card_tags">
          <h2 className="movie_news_card_tag">{item.tag}</h2>

          <h2 className="movie_news_card_year">{item.year}</h2>
        </div>
        <h2 className="movie_news_card_title">
          {item.title}
        </h2>
      </div>
    );
  });

  const carousel=listOfNewMovies.slice(firstIndex,lastIndex).map((item)=>{
    return(
      <div className="movie_news_card" key={item.id}>
        <img
          src={require("../images/movie1.jpg")}
          className="movie_news_card_image"
          alt=""
        />
        <div className="movie_news_card_tags">
          <h2 className="movie_news_card_tag">{item.tag}</h2>

          <h2 className="movie_news_card_year">{item.year}</h2>
        </div>
        <h2 className="movie_news_card_title">
          {item.title}
        </h2>
      </div>
    )
  })




  return (
    <div>


      <div className="movie_news">
        <h1 className="movie_news_title">Upcoming Movies</h1>
        <div className="movie_news_cards">


        {listOfNewMovies}


        </div>

        <button onClick={prevPage} className="pagination-button">
        <BiChevronLeft />
      </button>
      <button onClick={nextPage} className="pagination-button">
      <BiChevronRight />
    </button>

        <hr></hr>
      </div>








      <div className="movie_news">
        <h1 className="movie_news_title pick">What to Watch</h1>
        <h2 className="movie_news_title_pick"> Top Picks </h2>

        <div className="movie_news_cards">
        {listOfPicks}
        </div>
        <hr></hr>
      </div>
    </div>
  );
}
