import React from 'react'

function EditMovie() {

    const [movie, setMovie] = useState("");
  const [rating, setRating] = useState("");
  const [description, setDescription] = useState("");
  const [imdb_url, setImdb_Url] = useState("");
  const [year, setYear] = useState("");
  const [tags, setTags] = useState("");
  const [star, setStar] = useState("");



  return (
    <div className="movie_form">
      <h3>Edit Movie</h3>

      <div className="movie_form_card">
        <form action="" onSubmit={handleSubmit}>
          <input
            type="text"
            required
            name="movie"
            id=""
            placeholder="Name of Movie..."
            value={movie}
            onChange={(e) => {
              setMovie(e.target.value);
            }}
          />
          <input
            type="number"
            required
            name="movie"
            id=""
            placeholder="Rating of Movie..."
            onChange={(e) => {
              setRating(e.target.value);
            }}
            value={rating}
          />
          <input
            type="number"
            required
            name="movie"
            id=""
            placeholder="Year of Movie..."
            onChange={(e) => {
              setYear(e.target.value);
            }}
            value={year}
          />
          <textarea
            type="text"
            required
            rows={5}
            name="movie"
            className="movie_form_textarea"
            id=""
            placeholder="Description of Movie..."
            onChange={(e) => {
              setDescription(e.target.value);
            }}
            value={description}
          />
          <input
            type="text"
            required
            name="movie"
            id=""
            placeholder="ImDB URL..."
            value={imdb_url}
            onChange={(e) => {
              setImdb_Url(e.target.value);
            }}
          />
          <input
            type="text"
            required
            name="movie"
            id=""
            placeholder="Tags..."
            value={tags}
            onChange={(e) => {
              setTags(e.target.value);
            }}
          />
          <input
            type="text"
            required
            name="movie"
            id=""
            placeholder="Actors ( , separator )"
            value={star}
            onChange={(e) => {
              setStar(e.target.value);
            }}
          />

        </form>
        <div className="movie_form_button_container">
        <button onClick={handleSubmit} type="movie_form_submit">Add Movie</button>

        </div>
      </div>
    </div>
  )
}

export default EditMovie
